datos = load('FlujoVehicular2019.dat');function movTotalPorHora = acumularMovilidadPorHora(datos)    movTotalPorHora = zeros(24,1);        for i = 1:rows(datos)      movTotalPorHora(datos(i,3)+1) += datos(i,9);    endfor       disp('Movilidad por hora');    disp(movTotalPorHora);    disp('Fin movilidad por hora');
  endfunction  #Son 22 franjas de 3 horas, la primera franja es desde las 0 hasta las 2, la segunda desde la 1 hasta las 3 y asi... function movilidadPorFranja = acumularMovilidadPorFranja(movilidadPorHora)      movilidadPorFranja = zeros(22,2);   for i = 1: rows(movilidadPorFranja)     movilidadPorFranja(i,1) = i;     movilidadPorFranja(i,2) = movilidadPorHora(i) + movilidadPorHora(i+1) + movilidadPorHora(i+2);   endfor
      disp('Movilidad por franja');   disp(movilidadPorFranja);   disp('Fin movilidad por franja');   endfunction function franjasConMayorMovilidad = obtenerFranjasConMayorMovilidad(movilidadPorFranja)   franjasConMayorMovilidad = zeros(2,2);      for i = 1:rows(movilidadPorFranja)     if (movilidadPorFranja(i,2) > franjasConMayorMovilidad(1,2))       franjasConMayorMovilidad(1,1) = movilidadPorFranja(i,1);       franjasConMayorMovilidad(1,2) = movilidadPorFranja(i,2);     endif
   endfor      for i = 1:rows(movilidadPorFranja)     if (movilidadPorFranja(i,2) > franjasConMayorMovilidad(2,2)) && (movilidadPorFranja(i,1) > (franjasConMayorMovilidad(1,1) + 3)) || (movilidadPorFranja(i,1) < (franjasConMayorMovilidad(1,1) - 3))       franjasConMayorMovilidad(2,1) = movilidadPorFranja(i,1);       franjasConMayorMovilidad(2,2) = movilidadPorFranja(i,2);     endif   endfor   
   disp('Franjas con mayor movilidad');   disp(franjasConMayorMovilidad);endfunction
  plot(0:23, acumularMovilidadPorHora(datos),'--o'); title('Movilidad total por hora'); obtenerFranjasConMayorMovilidad(acumularMovilidadPorFranja(acumularMovilidadPorHora(datos))); 